# -*- coding: utf-8 -*-
import sqlite3
import os



def connect_db():
    """
    Fonction pour établir une connexion à la base de données SQLite.
    """
    return sqlite3.connect('QCM.db')

def recreate_tables():
    if os.path.exists('QCM.db'):
        os.remove('QCM.db')

    conn = connect_db()
    cur = conn.cursor()

    # Suppression des tables existantes
    cur.execute("DROP TABLE IF EXISTS QI;")
    cur.execute("DROP TABLE IF EXISTS reponses;")
    cur.execute("DROP TABLE IF EXISTS commentaires;")
   

    # for i in range(1, 8):
    #     cur.execute(f"DROP TABLE IF EXISTS DP_{i};")

    # Création des tables
    cur.execute("""
        CREATE TABLE QI (
            id_QI INTEGER PRIMARY KEY AUTOINCREMENT,
            specialite TEXT NOT NULL,
            item TEXT NOT NULL,
            rang TEXT NOT NULL,
            enonce TEXT NOT NULL,
            prop1 TEXT NOT NULL,
            prop2 TEXT NOT NULL,
            prop3 TEXT NOT NULL,
            prop4 TEXT NOT NULL,
            prop5 TEXT NOT NULL,
            prop6 TEXT NOT NULL,
            prop7 TEXT NOT NULL,
            prop8 TEXT NOT NULL,
            prop9 TEXT NOT NULL,
            prop10 TEXT NOT NULL,
            ville TEXT NOT NULL DEFAULT 'Unknown',
            type_qcm TEXT NOT NULL DEFAULT 'QI',
            reponse_correcte TEXT NOT NULL DEFAULT '',
            correction TEXT NOT NULL,
            image_url TEXT NOT NULL DEFAULT '',
            zone_correcte TEXT NOT NULL DEFAULT '',
            zone_correcte_2 TEXT NOT NULL DEFAULT '',
            zone_correcte_3 TEXT NOT NULL DEFAULT '',
            zone_correcte_4 TEXT NOT NULL DEFAULT '',
            image_url_correction TEXT NOT NULL DEFAULT '',
            commentaire_prop1 TEXT NOT NULL,
            commentaire_prop2 TEXT NOT NULL,
            commentaire_prop3 TEXT NOT NULL,
            commentaire_prop4 TEXT NOT NULL,
            commentaire_prop5 TEXT NOT NULL,
            commentaire_prop6 TEXT NOT NULL,
            commentaire_prop7 TEXT NOT NULL,
            commentaire_prop8 TEXT NOT NULL,
            commentaire_prop9 TEXT NOT NULL,
            commentaire_prop10 TEXT NOT NULL,
            date TEXT NOT NULL,
            session TEXT NOT NULL,
            is_EDN INTEGER,
            is_etu INTEGER,
            is_FAQ INTEGER
            
        );
    """)

    cur.execute("""
        CREATE TABLE IF NOT EXISTS reponses (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            id_question INTEGER NOT NULL,
            utilisateur_id INTEGER NOT NULL,
            reponse_utilisateur TEXT NOT NULL,
            note INTEGER NOT NULL,
            date_reponse TEXT NOT NULL,
            FOREIGN KEY (id_question) REFERENCES QI(id_QI),
            FOREIGN KEY (utilisateur_id) REFERENCES users(utilisateur_id)
        );
    """)

    cur.execute("""
        CREATE TABLE IF NOT EXISTS DP_0 (
            id_DP INTEGER PRIMARY KEY AUTOINCREMENT,
            specialite TEXT NOT NULL,
            item TEXT NOT NULL,
            rang TEXT NOT NULL,
            enonce_accueil TEXT NOT NULL,
            ville TEXT NOT NULL DEFAULT 'Unknown',
            image_url TEXT NOT NULL DEFAULT '',
            date TEXT NOT NULL,
            session TEXT NOT NULL,
            origine TEXT NOT NULL DEFAULT '',
            type_qcm TEXT NOT NULL DEFAULT '',
            is_kfp INTEGER TEXT NOT NULL,
            is_EDN INTEGER,
            is_etu INTEGER,
            is_FAQ INTEGER
        );
    """)
    cur.execute("""
     CREATE TABLE IF NOT EXISTS user_results (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            user_id INTEGER NOT NULL,
            qcm_id INTEGER NOT NULL,
            date TEXT NOT NULL,
            note INTEGER NOT NULL,
            FOREIGN KEY (qcm_id) REFERENCES QI(id_QI)
        );
""")
    cur.execute("""

    CREATE TABLE IF NOT EXISTS user_answers (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    user_id INTEGER NOT NULL,
    question_id INTEGER NOT NULL,
    answer TEXT NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(id),  -- Assurez-vous que la table 'users' existe
    FOREIGN KEY (question_id) REFERENCES DP_1(id_DP)  -- Assurez-vous que cette relation est cohérente avec la table DP
);    """)



    # Création de la table users avec la structure correcte
    cur.execute("""
        CREATE TABLE users (
            utilisateur_id INTEGER PRIMARY KEY AUTOINCREMENT,
            nom TEXT NOT NULL,
            prenom TEXT NOT NULL,
            sexe TEXT NOT NULL,
            mail_universitaire TEXT NOT NULL UNIQUE,
            pseudo TEXT NOT NULL UNIQUE,
            faculte TEXT NOT NULL,
            annee_medecine INTEGER NOT NULL,
            mot_de_passe TEXT NOT NULL,
            otp_code TEXT,
            otp_expiration INTEGER,
            role TEXT, 
            is_admin BOOLEAN DEFAULT FALSE
        );
    """)
    # Création des tables DP_1 à DP_7
    for i in range(1, 9):
        cur.execute(f"""
        CREATE TABLE IF NOT EXISTS DP_{i} (
            id_DP INTEGER PRIMARY KEY AUTOINCREMENT,
            type TEXT NOT NULL,
            enonce TEXT NOT NULL,
            prop1 TEXT NOT NULL,
            prop2 TEXT NOT NULL,
            prop3 TEXT NOT NULL,
            prop4 TEXT NOT NULL,
            prop5 TEXT NOT NULL,
            prop6 TEXT NOT NULL,
            prop7 TEXT NOT NULL,
            prop8 TEXT NOT NULL,
            prop9 TEXT NOT NULL,
            prop10 TEXT NOT NULL,
            prop11 TEXT NOT NULL,
            prop12 TEXT NOT NULL,
            reponse_correcte TEXT NOT NULL,
            image_url TEXT NOT NULL DEFAULT '',
            zone_correcte TEXT NOT NULL DEFAULT '',
            commentaire TEXT NOT NULL,
            image_url_correction TEXT NOT NULL,
            zone_correcte_2 TEXT NOT NULL DEFAULT '',
            zone_correcte_3 TEXT NOT NULL DEFAULT '',
            zone_correcte_4 TEXT NOT NULL DEFAULT '',
            commentaire_prop1_DP TEXT NOT NULL,
            commentaire_prop2_DP TEXT NOT NULL,
            commentaire_prop3_DP TEXT NOT NULL,
            commentaire_prop4_DP TEXT NOT NULL,
            commentaire_prop5_DP TEXT NOT NULL,
            commentaire_prop6_DP TEXT NOT NULL,
            commentaire_prop7_DP TEXT NOT NULL,
            commentaire_prop8_DP TEXT NOT NULL,
            commentaire_prop9_DP TEXT NOT NULL,
            commentaire_prop10_DP TEXT NOT NULL,
            commentaire_prop11_DP TEXT NOT NULL,
            commentaire_prop12_DP TEXT NOT NULL,
            FOREIGN KEY (id_DP) REFERENCES DP_0(id_DP)
           
        );
    """)

    cur.execute("""
    CREATE TABLE IF NOT EXISTS commentaires (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    commentaire TEXT NOT NULL,
    date TEXT NOT NULL
);
""")
    
  
    cur.execute("""

   CREATE TABLE IF NOT EXISTS commentaires_QI (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    commentaire TEXT NOT NULL,
    date TEXT NOT NULL,
    parent_id INTEGER,
    pseudo TEXT NOT NULL,
    qcm_id INTEGER NOT NULL,
    FOREIGN KEY (qcm_id) REFERENCES QI(id_QI)
);
""")


   

    # Commit des modifications et fermeture de la connexion
    conn.commit()
    conn.close()



